/*------------------------------------------------------------------------------
------
------    Functions
------
------------------------------------------------------------------------------*/
parameter_desc_t * binarySearch(parameter_desc_t elements[],
    uint16_t low, uint16_t high, uint16_t target)
{

    uint16_t pos = 0;
    low -= 1; /**< array index*/
    high -= 1;

    while(low <= high) {
		pos = (low + high) / 2;
		if( elements[pos].md_index == target)
			return &elements[pos];
		if( elements[pos].md_index > target)
			high = pos - 1;
		else
			low = pos + 1;
	}
    return PARAM_NULL;
}
